{
  "name": "PT Teknologi Nusantara Updated",
  "address": "Jl. Gatot Subroto No. 456, Jakarta",
  "description": "Updated company description",
  "isActive": true
}

{
  "name": "Software Engineer",
  "description": "Bertanggung jawab untuk mengembangkan dan memelihara aplikasi",
  "isActive": true
}

{
  "name": "Senior Software Engineer",
  "description": "Updated description for senior role",
  "isActive": true
}

{
  "title": "Formulir Permintaan Layanan",
  "description": "Formulir untuk mengajukan permintaan layanan baru",
  "formType": "intake",
  "fields": [
    {
      "order": 1,
      "label": "Nama Lengkap",
      "type": "text",
      "required": true,
      "placeholder": "Masukkan nama lengkap Anda"
    },
    {
      "order": 2,
      "label": "Email",
      "type": "email",
      "required": true,
      "placeholder": "email@example.com"
    },
    {
      "order": 3,
      "label": "Jenis Layanan",
      "type": "select",
      "required": true,
      "options": [
        {
          "key": "web_dev",
          "value": "Website Development"
        },
        {
          "key": "mobile_dev",
          "value": "Mobile App Development"
        }
      ]
    },
    {
      "order": 4,
      "label": "Budget (dalam juta rupiah)",
      "type": "number",
      "required": true,
      "min": 10,
      "max": 1000
    }
  ]
}

{
  "title": "Formulir Work Order",
  "description": "Formulir untuk detail pekerjaan",
  "formType": "work_order",
  "fields": [
    {
      "order": 1,
      "label": "Analisis Kebutuhan",
      "type": "textarea",
      "required": true
    },
    {
      "order": 2,
      "label": "Progress (%)",
      "type": "number",
      "required": true,
      "min": 0,
      "max": 100
    }
  ]
}

{
  "title": "Formulir Laporan",
  "description": "Formulir untuk laporan progress",
  "formType": "report",
  "fields": [
    {
      "order": 1,
      "label": "Laporan Progress",
      "type": "textarea",
      "required": true
    },
    {
      "order": 2,
      "label": "Kendala",
      "type": "textarea",
      "required": false
    }
  ]
}

{
  "title": "Website Development Service",
  "description": "Layanan pembuatan website profesional untuk bisnis Anda",
  "requiredStaffs": [
    {
      "positionId": "{{positionId}}",
      "minimumStaff": 1,
      "maximumStaff": 3
    }
  ],
  "workOrderForms": [
    {
      "order": 1,
      "formId": "{{formId}}",
      "fillableByRoles": ["manager_company", "staff_company"],
      "viewableByRoles": ["manager_company", "staff_company", "owner_company"]
    }
  ],
  "reportForms": [
    {
      "order": 1,
      "formId": "{{formId}}",
      "fillableByRoles": ["staff_company"],
      "viewableByRoles": ["manager_company", "staff_company", "owner_company"]
    }
  ],
  "accessType": "public",
  "isActive": true,
  "clientIntakeForms": [
    {
      "order": 1,
      "formId": "{{formId}}"
    }
  ]
}

{
  "description": "Updated description for work order",
  "priority": "medium",
  "notes": "Work order sedang dikerjakan oleh tim"
}
